int getFinalFloor(string input) {
  var up = input.Count(c => c == '(');
  var down = input.Count(c => c == ')');
  return up - down;
}

int getIndexForFloor(string input, int targetFloor) {
  var currentFloor = 0;
  for (var i = 0; i < input.Length; i++) {
    if (input[i] == '(')
      currentFloor++;
    else
      currentFloor--;
    if (currentFloor == targetFloor)
      return i+1;
  }
  return 0;
}